style.css:
@-moz-document domain(google.com)
{

div, a, p, img, ul, li, span, button, input, body, html {
border-radius: 100000px !Important;
    transform: rotate(90deg);
animation: .7s flashlol infinite;
    
}

@keyframes flashlol {
    20% { background: red; color: red;}
    40% { background: yellow; color: yellow}
    60% { background: green; color: green;}
    80% { background: blue; color: blue;}
    100% { background: purple; color: purple;}
}

}
