style.css:
@-moz-document domain("dict.youdao.com"), domain("youdao.com") {
.ads,
.result_navigator{
    display: none;
}

#results{
    float: none;
    margin: 0 auto;
}

#scontainer{
	margin-top: 91px;
}

.c-topbar-wrapper.setTop{
    position: absolute;
    top: -145px;
}

#phrsListTab ~ *{
    opacity: .4;
    transition: .2s;
}

#phrsListTab ~ *:hover{
	opacity: 1;
}

.trans-container li{
    font-size: 16px;
    line-height: 30px;
}
}
