style.css:
@-moz-document domain("btc-trade.com.ua") {
  body {
    background: #2e2e3a !important;
    color: #bbb !important;
}

.border_radius {
   	background: #202028 !important;
    border-color: #2b2d39 !important;
    border-radius: 4px 4px 4px 4px;
}

.navbar-inverse {
    background-color: #23232d !important;
}

.btn-success {
    color: #bbb !important;
    background-color: #111 !important;
    border-color: #313131 !important;
}

.btn-default {
    color: #999;
    background-color: #3e3e4a !important;
    border-color: #2b2d39;
}

.modal-content {
    background: #2e2e3a !important;
    color: #bbb !important;
}

.close {
	color: #bbb !important;   
}

.form-control {
    color: #bbb !important;
    background-color: #2e2e3a;
    border: 1px solid #333;
}

#sidebar_twitter {
    background-color: #2e2e3a;
}

.table {
	background-color: #2F2F40;
}

.cursor {
	background-color: #2F2F40 !important;
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #2F2F40;
}
}
