style.css:
/* **************************** */
/* Minimal Facebook without Ads */
/* **************************** */
/* Hide: right column (#rightCol), most of left column (#appsNav, #createNav). */
/* Theme color:</b> use background color(#e9ebef) for banner.  */

@-moz-document domain("facebook.com") {
  /* HIDDEN BLOCKS */
  #xsideNav, #appsNav, #createNav, #rightCol>div {
      display: none !important;
  }
  /* BANNER COLOR */
  [role="banner"] {
      background-color: #e9ebef !important;
      border-bottom: none !important;
      color: grey !important;
  }
  /* SEARCH FIELD */
  [role="search"] {
      border: none !important;
  }
}
