	.view-comment .view-comment-sub .sub-manga {
		margin-top: 10px;
		border-radius: 2mm
	}

	body:not(.white) .view-comment .view-comment-sub .sub-manga {
		background: #333
	}

	body:not(.white) .view-comment .view-comment-sub .sub-manga .top,
	body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .title a,
	body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .subtitle-new,
	body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .tip {
		color: #bbb
	}

	body:not(.white) .view-comment .view-comment-sub .sub-manga,
	body:not(.white) .view-comment .view-comment-sub .sub-manga .top,
	body:not(.white) .view-comment .view-comment-sub .sub-manga .list li {
		border-color: #666
	}

