	.mh-item {
		border-radius: 1.5mm;
		box-shadow: 0 0 1mm #888
	}

	.mh-cover {
		border-radius: 1.5mm 1.5mm 0 0
	}

	.mh-item .mh-item-detali {
		background: #eaeaea;
		overflow: hidden;
		padding: 0 1.5mm 2mm;
		border-radius: 0 0 1.5mm 1.5mm
	}

	.mh-item .mh-item-detali .title {
		text-align: center;
		padding: 1mm 0 2mm 0;
		margin: 0 !important
	}

	.mh-item .mh-item-detali .title a {
		display: inline-block;
		max-width: calc(100% - .3em);
		font: 400 calc(9pt + .2vw) sans-serif;
		line-height: 1.4;
		color: #334;
		overflow: hidden;
		text-overflow: ellipsis;
		padding: 0 .4em;
		border-radius: 1em;
		transition: .5s
	}

	.mh-item .mh-item-detali .title a:hover {
		color: navy;
		background: rgba(255, 255, 255, .6);
		box-shadow: 0 0 1mm navy;
		transition: .1s
	}

	.mh-item .mh-item-detali .title a:active {
		color: #eef;
		background: #777;
		box-shadow: inset 0 1px 1mm #000;
		transition: 0s
	}

	.mh-item .mh-tip-wrap {
		top: -10%;
		max-height: none;
		padding: 0;
		overflow: hidden;
		border-radius: 2mm;
		transition: width 0s, opacity .3s, transform .2s, top .1s
	}

	.mh-item .mh-tip-wrap.active {
		top: 0;
		width: 226px;
		height: 330px;
		box-shadow: 0 0 5mm #555
	}

	.mh-item-tip {
		margin: 0 auto
	}

	.view-win {
		box-shadow: 0 2px 5mm rgba(0, 0, 0, .4)
	}

	.view-mask {
		opacity: .6;
		visibility: visible;
		transition: opacity .3s, visibility 0s
	}

	.view-paging a,
	.view-paging span {
		font-size: 11pt;
		background: #444;
		border-radius: 6px;
		box-shadow: inset 0 0 0 1px rgba(0, 0, 0, .4)
	}

	.view-comment-main .child-list {
		margin: .3em 0 !important;
		background: rgba(155, 155, 155, .1) !important;
		box-shadow: 0 0 0 1px rgba(255, 255, 255, .2)
	}

	body.white .view-comment-main .child-list {
		background: rgba(100, 100, 100, .1) !important;
		box-shadow: 0 0 0 1px rgba(0, 0, 0, .2)
	}

	.box-con-5n1 .detail-tabs {
		right: 5px
	}

	.box-con-5n1 .detail-tabs-container .detail-tabs-pane {
		width: 100%;
		padding: 8px
	}

	.box-con-8n3 .box-8n3-pane {
		background-color: transparent
	}

	.box-con-8n3 .box-8n3-pane .box-cat-list li {
		margin-bottom: 3mm
	}

	.js_box_pane {
		padding: 3mm;
	}

	.article-main {
		width: calc(100% - 250px)
	}

	.page-pagination ul li a {
		background: #eee
	}

	.alertTop_1 {
		height: 4em
	}

	.index-right-float {
		margin-left: calc(50vw - 16mm);
		border-radius: 5px;
		box-shadow: 0 2px 3mm #666;
		opacity: .4;
		transition: .2s
	}

	.index-right-float.bottom {
		bottom: 30mm
	}

	.index-right-float:hover {
		opacity: 1
	}

	.view-win .title .view-code,
	.view-win .title .view-collection {
		padding: 3px 0 3px 24px
	}

	.view-win .title .view-btn-back {
		background: #f5f5fc;
		border: none
	}

	.view-win .title .view-btn-back,
	.view-win .title .view-btn-next {
		border-radius: 5px;
		box-shadow: 0 0 0 1px #bbb
	}

	.view-win .title .view-btn-back:hover,
	.view-win .title .view-btn-next:hover {
		box-shadow: inset 0 0 0 1px #ddd, 0 0 5px #22c
	}

	.new-search-banner {
		height: auto
	}

	.new-search-input-form {
		margin: 5mm auto 2mm;
		height: auto
	}

	.new-search-input-form input,
	.new-search-input-form-btn-1,
	.new-search-input-form-btn-2 {
		height: auto;
		line-height: 2;
		vertical-align: middle;
		padding-top: 1mm;
		padding-bottom: 1mm;
		background-position: 16px 50%;
		box-shadow: 0 0 0 1px #999
	}

	.new-search-input-form input {
		border-radius: 2mm 0 0 2mm
	}

	.new-search-input-form-btn-2 {
		border-radius: 0 2mm 2mm 0
	}

	.new-search-list {
		top: 36px
	}

	.new-search-font {
		top: 0px;
		left: 70%
	}

	.hover .block,
	.right-bar .read-style-con .info {
		border: none !important;
		border-radius: 2mm !important;
		text-shadow: none;
		box-shadow: 0 2pt 4mm rgba(0, 0, 0, .3) !important
	}

	.view-comment .view-comment-sub {
		position: relative !important;
		left: 0 !important;
		bottom: 0 !important;
		margin-left: 0 !important
	}

	.detail-list-1 li a {
		border-radius: 2mm
	}

	.detail-list-1 li a:hover {
		background: #eef;
		box-shadow: 0 0 0 1px #aa2
	}

	@media screen and (max-width: 900pt) {
		.box .mh-list.col7>li {
			width: calc(100%/5 - 4mm);
		}

		.mh-item .mh-tip-wrap {
			top: -17%;
			left: -21%;
			width: 143%;
			height: 107%;
			transform: scale(.7);
		}

		.mh-item .mh-tip-wrap,
		.mh-item-tip {
			max-width: 210px;
		}

		.mh-item .mh-tip-wrap.active {
			top: -10px;
			left: calc(50% - 105px);
		}

		.banner_detail_form .info {
			width: calc(100% - 300px);
		}
	}

	@media screen and (min-width: 900pt) {
		.box .mh-list.col7>li {
			width: calc(100%/6 - 4mm);
		}

		.mh-item .mh-tip-wrap {
			top: -17%;
			left: -21%;
			width: 143%;
			height: 110%;
			transform: scale(.7);
		}

		.mh-item .mh-tip-wrap.active {
			top: -12px;
			left: calc(50% - 113px);
		}
	}

	@media screen and (min-width: 1050pt) {
		.box .mh-list.col7>li {
			width: calc(100%/7 - 4mm);
		}

		.mh-item .mh-tip-wrap {
			top: -8%;
			left: -11%;
			width: 122%;
			height: 92%;
			transform: scale(.82);
		}

		.mh-item .mh-tip-wrap.active {
			top: -2px;
		}
	}

	@media screen and (min-width: 1250pt) {
		.box .mh-list.col7>li {
			width: calc(100%/8 - 4mm);
		}

		.mh-item .mh-tip-wrap {
			left: -9%;
			width: 118%;
			transform: scale(.85);
		}

		.mh-item .mh-tip-wrap.active {
			top: 15px;
			transform: scale(1.1);
		}
	}

	@media screen and (min-width: 132vh) {
		#barChapter img {
			box-shadow: none;
		}

		#barChapter img:nth-of-type(odd) {
			border-radius: 2mm 0 0 2mm;
		}

		#barChapter img:nth-of-type(even) {
			border-radius: 0 2mm 2mm 0;
		}
	}
}
