/* ==Nappsel Styles==
@name Nappsel's CSS Fixes
@namespace userstyles.org/styles/154856/nappsels-css-fixes
@version 0.20180724.1
@author nappsel
==/Nappsel Styles== */

@-moz-document domain("userstyles.org"), domain("lostfilm.tv") {
body.lstfml {background:#1c1e1f}
body a[href*="/"] > span[class*="_"] {display:none}
.right-pane > .oadd {display:none}
}

@-moz-document domain("onlinetrade.ru") {
.content .adw_head,
.content .content_shiftAdw {display:none}
}

@-moz-document domain("xenforo.info") {
.p-body > .p-body-inner > div[align*="center"] {display:none!important}
}

@-moz-document domain("ctc.ru") {
div.inner-container {margin:15px 0 !important}
.project-banner#banner_right, .comments-container .project-banner, .html-block_bottom > div[class*='banner-wrapper-'] {display:none!important}
div.inner-container > div.tv-programm-container, .widget.comments .comments-subtitle, .widget.comments .load-comments, .widget.comments .comments-wrapper {width:100%}
.widget.comments .comments-wrapper .cwi-comment {width:93%}

div.future-programm .tv-programm-item {padding: 15px 12px}
div.future-programm .tv-programm-item > .tpi-info {display: flex; flex-flow: row nowrap; align-items: center}
div.future-programm .tv-programm-item > .tpi-info > .tv-info-time {display: flex; flex: 1 0 15%}
div.future-programm .tv-programm-item > .tpi-info > .tv-info-inner {display: flex; flex: 0 0 80%}
div.future-programm .tv-programm-item > .tpi-info > .tv-info-inner > a {width: 100%; max-width: 100%; margin: 0; padding: 10px}
div.future-programm .tv-programm-item > .tpi-info > .tv-info-inner .tpi-genre {display: flex; flex: 0 0 50%; width: auto; position: inherit; padding: 10px}
div.future-programm .tv-programm-item > .tpi-info > .tpi-age-limit {margin-top: 0}

div.widget-wrapper .widget-banner {display:none!important}
div.widgets-block_index > .padding-fluid {padding:0}

div.container .video-page .help, div.project-container > .project-info .help, div.project-container > div.widget.special-block {display:none!important}
div.container .video-page .video-player-wrapper {width:100%; padding: 15px; margin:0}
div.project-container > .project-info .project-player-wrapper {width:100%}
div.container .video-page [id="container_new-html5-player"] iframe, div.project-container > .project-info [id="container_new-html5-player"] iframe {height:557px !important}
}

@-moz-document domain("steambuy.com") {
body {background: #f2f2f2 !important}
div > div > img#imgBnUrl {display:none!important}
.container > main > img[src*='/banner'] {display:none!important}
}

@-moz-document domain("winscp.net") {
.promo-block {display:none}
}

@-moz-document domain("cinemapark.ru") {
#page > .promo,
#page > #container > .branding,
.schedule_widget__banner {display:none!important}
#page > #container > #content .cinema-page__cinema-item .cinema-page__cinema-item__right {display:none!important}
#page > #container > #content .cinema-page__cinema-item .cinema-page__cinema-item__left .cinema-page__cinema-item__media {width: 15%}
}

@-moz-document domain("fox-fan.ru"), domain("fox-fan.tv") {
.centerBody {padding: 5px 0 5px 5px}
.rightBody {width:100%; padding:5px; margin:0}
.rightBody > #rightBlock {background:none; width:100%; margin:0; padding:0}
.rightBody .left_panel_shop {display:none}
.centerBody #centerSeries > #regBlock {margin-top:20px}

.centerBody #centerSeries > #buttomBack,
.centerBody #centerSeries > #buttomNext {width:50%}

.centerBody #centerSeries,
.centerBody #centerSeries > #regBlock,
.centerBody #centerSeries > div[id*="player"],
.centerBody #centerSeries > #content > #comtab table {width:100%!important}

.centerBody #leftSeries,
.centerBody #centerSeries > #noVip,
.centerBody #centerSeries > #Shop,
.centerBody #centerSeries > #likeBlock,
.centerBody #centerSeries > br[clear*="all"] {display:none}
}

@-moz-document domain("kinopoisk.ru") {
table div[data-type*="teaserspec"].rightTeaser,
table div.right-sidebar .right-sidebar__banner {display:none}
body div#content_block > a.film-thin-teaser {display:none!important}
}

@-moz-document domain("overbuff.com") {
body div[class*="kunkka"] {display:none!important}
}

@-moz-document domain("gosugamers.net") {
body > #page > #horizontal-holder.container,
body > #page > div.container [id*="box-holder"].holder,
body > #page .footer > #lower-lb-holder,
body > #page .footer > .footer-top-links > .foot-header > .col-5 {display:none!important}
body > #page .footer > .footer-top-links > .foot-header > .col-7 {width:100%}
body > #page .footer > .footer-top-links > .foot-header > .col-7 > .gg-row {padding:15px 0 15px 0}
}

@-moz-document domain("masteroverwatch.com") {
div[class*="ad-unit"], [class*="tools-link-masterpubg"],
.row > div.col-md-4 > div[style*="background:rgba(0, 0, 0, 0.1)"] {display:none!important}
}

@-moz-document domain("habrahabr.ru"), domain("habr.com") {
.dfp-slot, .promo-block {display:none}
}

@-moz-document domain("hh.ru") {
div[class*="HHC-Banner"],
div.HH-MainContent > div.g-row.m-row_content > .g-col4 {display:none!important}
div.HH-MainContent > div.g-row.m-row_content > .m-colspan3 {width:100%}
}

@-moz-document domain("rutube.ru") {
#page-content > div.b-div#b-div {display:none!important}
}

@-moz-document domain("twitter.com") {
.stream li[data-item-type="recap_entry"],
.stream li[data-item-type="who_to_follow_entry"] {display:none!important}
}

@-moz-document domain("forums.goha.ru") {
div.body_border > div > table td[align="left"] {display:none!important}
}
@-moz-document url-prefix("https://yandex.ru/news") {
body > div:nth-child(3) {
    margin: auto;
}
body > div:nth-child(3) > div:nth-child(2) {
    display: none !important;
}
}
