@-moz-document domain("re-library.com") {
/* Site Background */
body {
    /*[[bgClr]]*/
}
/* Main Body */
#primary,
.site-content,
.entry-header,
.entry-meta,
.entry-content {
    background: #232323 !important;
    color: #eee !important;
}
.entry-title,
.entry-title a {
    color: lightslategray !important;
    font-style: italic !important;
}
/* Tags & Bottom Nav Links */
.cat-links a,
.nav-links a {
    color: #aaa !important;
}
/* For those with adblocker */
.ai-viewport-1,
.adsbygoogle {
    background-color: #232323 !important;
}
}
