style.css:
@-moz-document url-prefix("https://userstyles.org") {
body {
	background: #2D2D30;
}
div#nav-view, div#navigation {
	background: #2D2D30;
}
div#footer, #header {;
	background: #1C1C1C;
}
.navigation_link > *, .pageNumber, p, .style_author_long, #information_key_left, #infomation_value_left, #share_div, .text_style_page, #information_key, #information_value, table, ul, li, dt {
    color: #E0DDF0;
    fill: #E0DDF0;
}
a, #applies_link {
    color: #3498db;
    text-decoration: none;
}
.navigation_link:hover {
    background:#1C1C1C;
}
.navigation_link_active {
    border-left: 3px solid white;
}
h1, h2, h3, h4, h5, h6, #title_long a, #headline {
    color: white !important;
}
}
