style.css:
@-moz-document domain("elixirschool.com") {
body {
    background-color: #212121;
    color: #fafafa;
}
h1, h2, h3, h4, h5, h6, .page-title {
    color: #fafafa;
}
strong {
    color: #fafafa;
}
.back-to-top, .theme-custom-purple .content a {
    color: #268bd2;
}
.highlight {
    background-color: #212121;
}
.highlight {
    color: #c5c8c6;
    font-weight: normal;
}
.highlight .k {
    color: #8959a8;
}
.highlight .no {
    color: #81a2be;
    font-weight: 700;
}
.highlight .sd, .highlight .ss, .highlight .s2, .highlight .si, .highlight .ow {
    color: #b5bd68;
}
.highlight .kn, .highlight .o {
    color: #c5c8c6;
}
code {
    background: #2C2C31;
}
}
