style.css:
@-moz-document domain("wunderlist.com") {
.main-interface , .edit-view {
    direction: rtl;
    font-family: iransans,Tahoma;
}
.expandingArea active , .addTask {font-family:iransans,Tahoma}


html #wunderlist-base [type=text], html #wunderlist-base [type=email], html #wunderlist-base [type=password], html #wunderlist-base .input-fake {font-family:iransans,Tahoma}


.expandingArea textarea, .expandingArea pre {font-family:iransans,Tahoma}
}
