style.css:
@-moz-document domain("uloz.to"), domain("ulozto.cz") {
.page-branding .pg-wrapper .pg-wrapper-bg {
    width:100% !important;
    margin:0 !important;
}
.page-branding .pg-wrapper {
    padding:0;
}
.page-branding .pg-wrapper .pg-wrapper-bg>.l-header {
    border:0;
}
.page-branding .pg-wrapper .pg-main {
    border:0;
    padding:0;
    padding-top:25px;
    padding-bottom:25px;
    width:100%;
}
.page-branding .pg-wrapper .pg-footer {
    border:0;
    margin:0;
    padding-left:25%;
    padding-right:25%;
    width:100%;
}

}
