style.css:
@-moz-document domain("raid.report") {
a {
    color: #1ac;
}

.drr-background {
    background-color: #223;
}

.card-content {
    background-color: #334;
    color: #ddd;
}

table.striped>tbody>tr:nth-child(odd) {
    background-color: #445;
}

.divider {
    background-color: #556;
    border-color: #556;
}

thead, table.bordered>thead>tr, table.bordered>tbody>tr {
    border-color: #556;
}

.jss12, .jss13, .jss30 {
    color: rgba(255,255,255,0.6);
}

.card-content, .card {
    background-color: #334;
    color: #ddd;
}
}
