style.css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
::-webkit-scrollbar
{
	/* Width */
	width: 1.2em!important;
}

/* Color of handle ___________________________________________________________________________*/
::-webkit-scrollbar-thumb
{
	background: #580303!important;
	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

::-webkit-scrollbar-thumb:hover
{
	background: #580303!important;
	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
	border-radius: 22px!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/* Color of Track ______________________________________________________________________________*/
::-webkit-scrollbar-track
{
	background: #170000!important;
	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

/* Color of handles at top and bottom ___________________________________________________________*/
::-webkit-scrollbar-button
{
	background: #4c0707!important;
	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}

::-webkit-scrollbar-button:hover
{
	border-radius: 22px!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*__________________________________________________________________________________*/
*
{
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

.CodeMirror-cursor
{
	border-left: 1px solid #fff!important;
}

#lint table
{
	border-spacing: 4px!important;
}

.search-result-description,
.search-result-meta
{
	position: relative!important;
}

#installed
{
	z-index: 99!important;
}

#installed,
.search-result-description
{
	overflow: visible!important;
}

#lint td[role="message"],
.search-result-description
{
	white-space: pre-wrap;
}

.entry .menu
{
	position: relative!important;
}

/*___ Fonts ________________________________________________________________________*/

body,
html
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.search-result-description
{
	font-size: 85%!important;
}

#lint tbody
{
	font-size: 83%!important;
}

.CodeMirror pre.CodeMirror-line,
.CodeMirror pre.CodeMirror-line-like,
.newUI .style-name
{
	font-size: 20px!important;
}

.colorpicker-input,
body,
button
{
	font-size: 16px!important;
}

#lint table
{
	line-height: 21px!important;
}

/*___ Remove Items ___________________________________________________________________________*/
ppp
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/
/* Top ______________________________*/
#help-popup.wide
{
	top: 18rem!important;
}

input[type="checkbox"]:not(.slider)
{
	top: 2px!important;
}

input[type="checkbox"]:not(.slider):checked + .svg-icon.checked
{
	top: 6px!important;
}

.svg-icon.select-arrow
{
	top: 12px!important;
}

.filter-selection .select-resizer
{
	top: -5px!important;
}

/* Right ______________________________*/
#help-popup.wide
{
	right: 47.8rem!important;
}

.svg-icon.select-arrow
{
	right: 9px!important;
}

/* Bottom ______________________________*/
/* Left ______________________________*/
.filter-selection
{
	left: -69px!important;
}

input[type="checkbox"]:not(.slider):checked + .svg-icon.checked
{
	left: 12px!important;
}

.filter-selection .select-resizer
{
	left: 80px!important;
}

#installed .style-name .checker
{
	left: 0!important;
}

#installed .style-name .svg-icon.checked
{
	left: 11px!important;
}

/* Margin-Top ______________________________*/
.aligned > *:not(.svg-inline-wrapper)
{
	margin-top: .5rem!important;
}

.search-result-description
{
	margin-top: 1rem!important;
}

#search-results
{
	margin-top: 0em!important;
}

#basic-info-enabled
{
	margin-top: 10px!important;
}

.newUI #installed
{
	margin-left: 29rem!important;
}

/* Margin-Right ______________________________*/
body > div:not(#installed):not(#message-box):not(.colorpicker-popup)
{
	margin-right: 29px!important;
}

/* Margin-Bottom ______________________________*/
/* Margin-Left ______________________________*/
body > div:not(#installed):not(#message-box):not(.colorpicker-popup)
{
	margin-left: 12px!important;
}

/* Padding-Top ______________________________*/
#installed
{
	padding-top: 10px!important;
}

/* Padding-Right ______________________________*/
/* Padding-Bottom ______________________________*/
/* Padding-Left ______________________________*/
#sections
{
	padding-left: 24%!important;
}

#basic-info-enabled label,
#header .options-column:first-child label,
#header .settings-column label,
.style-name
{
	padding-left: 2.5rem!important;
}

.style-edit-link,
.style-name a
{
	padding-left: 2rem!important;
}

#header .options-column label
{
	padding-left: 1rem!important;
}

#disable-all-wrapper .main-controls label,
#find-styles-inline-group label
{
	padding-left: 42px!important;
}

label
{
	padding-left: 40px;
}

.actions > .main-controls
{
	padding-left: 0!important;
}

/*___ Margins _______________________________________________________________________________*/
.filter-selection
{
	margin: 32px!important;
}

#header,
.settings-column
{
	margin: 12px!important;
}

.search-result,
.search-result-empty
{
	margin: 1rem!important;
}

#sections > div
{
	margin: 1rem 0!important;
}

.entry .menu-item > span
{
	margin: 1px 24px -7px!important;
}

/*___ Paddings _____________________________________________________________________*/
#help-popup,
.newUI #installed,
.options-column,
.search-result,
.search-result-empty,
.settings-column,
body > .actions
{
	padding: 12px!important;
}

input:not([type])
{
	padding: 14px 12px!important;
}

/*___ Heights ______________________________________________________________________*/
#installed,
html,
body
{
	max-height: none!important;
}

#help-popup .contents
{
	max-height: calc(100vh - 9rem)!important;
}

html,
body
{
	height: 70rem!important;
}

#header
{
	height: 97%!important;
}

.CodeMirror
{
	height: 84%!important;
}

#manage\.newUI\.sort,
#search
{
	height: 2rem!important;
}

select
{
	height: 38px!important;
}

input[type="checkbox"]:not(.slider)
{
	height: 20px!important;
}

.search-result,
.search-result-empty
{
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/
.select-resizer
{
	max-width: none!important;
}

#match
{
	width: 100%!important;
}

#installed
{
	width: 96%!important;
}

#header
{
	width: 23%!important;
}

.filter-selection .select-resizer select
{
	width: 17.5rem!important;
}

body,
body.search-results-shown
{
	width: 100%!important;
}

body > div:not(#installed):not(#message-box):not(.colorpicker-popup):not(.big)
{
	width: 25rem!important;
}

select
{
	width: 13rem!important;
}

#options [type="number"]
{
	width: 5.5em!important;
}

.colorpicker-popup
{
	width: 28rem!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*________________________________________________________________________*/
a[href],
td span
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

.dirty > label::before,
.error
{
	color: red!important;
}

/*________________________________________________________________________*/
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label
{
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________*/
#backup-message,
#confirm > div span,
#help-popup .contents,
#message-box.center #message-box-contents,
#write-style-for,
.beautify-options div,
.newUI .target,
.search-result-description,
dd,
li,
p,
td
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki ________________________________________________________________*/
#confirm > div > b,
#confirm > div b,
#help-popup .title,
#message-box-title,
.menu-title,
h1,
h2
{
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ____________________________________________________________*/
h3,
h4
{
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown __________________________________________________________*/
h5,
h6,
label
{
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/
time
{
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/
#no-styles
{
	color: #ff0707!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________*/
.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding: 3px 1rem!important;
}

/*________________________________________________________________________*/
::selection
{
	background: #ffff3e!important;
	color: #000!important;
}

pre span
{
	color: #fafbfb!important;
}

pre::selection
{
	background: #9eff99!important;
	color: #f0c46c!important;
}

.CodeMirror
{
	color: #ca1111!important;
}

.CodeMirror-activeline-gutter .CodeMirror-linenumber
{
	background-color: #000!important;
	color: #e9ee3c!important;
	font-weight: 700!important;
}

.CodeMirror-selected
{
	background: #ff00004f!important;
}

.cm-atom
{
	color: #aafad6!important;
}

.cm-comment
{
	color: #faf7c7!important;
}

.cm-keyword
{
	color: #d603fa!important;
}

.cm-number
{
	color: #76c2ff!important;
}

.cm-property
{
	color: #7fff6e!important;
}

.cm-qualifier
{
	color: #00ffe5!important;
}

.cm-s-default .cm-builtin
{
	color: #ded1fe!important;
}

.cm-s-panda-syntax .cm-property
{
	color: #ff7900!important;
}

.cm-s-panda-syntax .cm-string,
.newUI .has-favicons .target
{
	color: #19F9D8!important;
}

.cm-tag
{
	color: #ce0000!important;
}

.cm-variable-3
{
	color: #ec5881!important;
}

.svg-icon,
.svg-icon.info:hover,
.svg-icon.settings:hover
{
	fill: #fcf9f9!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*________________________________________________________________________*/
html,
body
{
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.CodeMirror-activeline-background
{
	background: #a2040447!important;
}

.CodeMirror-gutters
{
	background-color: #f7f7f70d!important;
}

dialog,
.applies-to
{
	background: #060505!important;
}

.resize-grip
{
	background: #360202!important;
}

.resize-grip:after
{
	border-top-color: #ff0303!important;
}

.CodeMirror
{
	border: solid #3f3d3d 1px;
}

/*___ Background-color 0000 ________________________________________________________*/
#filters label:hover,
#filters .filter-selection:hover,
#message-box-buttons,
#message-box-title,
.cm-s-panda-syntax,
:root
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__________________________________________________________________________________*/
.CodeMirror-scroll
{
	background: #000!important;
}

#help-popup .contents .CodeMirror pre
{
	background: #413d3d!important;
}

/*___ Highlight Background _________________________________________________________*/
.CodeMirror pre:hover
{
	background: -webkit-gradient(linear, left top, right top, from(#383838), to(#0000))!important;
	background: linear-gradient(to right, #3838385e, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Blue _________________________________________________________________________*/
button
{
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

button:hover
{
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

button:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/
#header,
#sections > div,
.newUI #installed
{
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#header:hover,
#sections > div:hover,
.newUI #installed:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/
#help-popup,
#message-box > div,
.CodeMirror-hints,
.colorpicker-popup,
.menu-items-wrapper,
.options-column,
.search-result,
.settings-column,
body > .actions
{
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#help-popup:hover,
#message-box > div:hover,
.CodeMirror-hints:hover,
.colorpicker-popup:hover,
.menu-items-wrapper:hover,
.options-column:hover,
.search-result:hover,
.settings-column:hover,
body > .actions:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Red Gradient ________________________________________________________________________*/
#confirm > div,
.disabled .actions,
.disabled .applies-to,
.disabled h2 .style-name-link,
.disabled h2::after
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

.CodeMirror-selected
{
	border-bottom: 1px solid #000!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

.newUI .entry:hover
{
	border-bottom: 1px solid #000!important;
	background: linear-gradient(to right, #240202 35%, #590505 100%)!important;
}

.search-result-meta
{
	background-color: hsla(0, 0%, 0%, 0.75)!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*__________________________________________________________________________________*/
.cm-s-panda-syntax .CodeMirror-linenumber,
.search-result,
.entry
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/
.cm-s-panda-syntax .CodeMirror-linenumber:hover,
.CodeMirror-activeline-gutter .CodeMirror-linenumber,
.entry:hover,
.search-result:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/
img
{
	border-radius: 22px!important;
}
}
