style.css:
@-moz-document domain(thalia.nu) {
	#main, #logo, #footer, #bottom, .post-inner, .nav-path-selected {
    background-color: #3a3a3a !important;
}

.nav-path-selected {
    background-color: #3a3a3a !important;
    color: #E62272 !important;
}

h1, h2, h3, h4, h5, h6, h7, p, label, .help-block, td, th {
    color: #ffeeee !important;
}

.post-body, post-excerpt p, #primary-navigation, li a {
    background-color: #4b4b4b !important;
    color: #ffeeee !important;
}
}
