style.css:
@-moz-document domain("itsfoss.com") {
    body, .site-inner
    {
        background-color: #1b1b1b !important;
        color: #e4e4e4 !important;
    }

    .site-header.light, .site-header, .footer-widgets, .site-footer, #thrive-comments .tcm-comments-list .tcm-comment-item.tcm-main-parent, #thrive-comments textarea, #thrive-comments input
    {
        background-color: #4b4a4a !important;
        color: #e4e4e4 !important;
    }

    .site-title a, .site-title a:hover, .genesis-nav-menu a, .footer-widgets h3, .footer-widgets h4, .footer-widgets .widget-title, #thrive-comments p, .tcm-comment-header .tcm-author-content .tcm-comment-author-name
    {
        color: #e4e4e4 !important;
    }

    .genesis-nav-menu a :hover
    {
        color: #baff97 !important;
    }

    .helpwidget 
    {
        background: #644f99 !important;
    }
}
