style.css:
@-moz-document domain("outlook.live.com")
{
 /* left side bottom ad */
div[id="app"] > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(2) {
    display: none !important;
}


/* right side ad */
div[id="app"] > div:nth-child(1) > div:nth-child(2)> div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4){
    display: none !important;
}

/* top ad in small screen */

@media only screen and (max-width: 990px) {
    div[id="app"] > div:nth-child(1)> div:nth-child(2)> div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(5){
        display: none !important;
    }
}
}
