style.css:
@-moz-document url-prefix("https://char-map.herokuapp.com/") {
#glyph {
    position: relative;
    left: auto;
    margin-bottom: 15px;
}

#glyph-bg {
    display: none;
}

#glyph-display {
    text-align: center;
}

#glyph-data > div > dd {
    text-transform: lowercase;
}
}
