@-moz-document url-prefix("https://www.inoreader.com/") {
#reader_pane{padding:0!important}.tiles_wrapper .ar{height:calc(50vh - 30px)!important;width:24.4%!important;min-width:none!important;max-width:none!important;margin:2px 0 2px 0!important}.tiles_wrapper .article_tile{background-color:#17171700!important}.article_tile_content_wraper{height:100%!important}.article_tile_content_wraper a div{-webkit-transition:-all .4s;transition:all .4s}div.article_tile:hover .article_tile_content_wraper a div{filter:brightness(.3) contrast(.9) blur(0)}.article_tile_picture{opacity:1!important;height:100%!important;-webkit-mask-image:linear-gradient(to bottom,transparent 0,#000 50%);mask-image:linear-gradient(to bottom,transparent 0,#000 50%)}.tiles_wrapper .article_title_link{position:absolute!important;top:0!important;margin:5px;padding-top:15px!important;line-height:1.5!important;text-shadow:-1px -1px 1px rgba(255,255,255,.1),1px 1px 1px rgba(0,0,0,.5);color:#fff!important;font-size:16px!important;opacity:.8!important}.article_tile_content{position:absolute!important;bottom:9%!important;overflow-y:hidden!important;height:64%!important}.article_tile_content_wraper{border-radius:10px!important;-webkit-mask-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAIAAACQd1PeAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAA5JREFUeNpiYGBgAAgwAAAEAAGbA+oJAAAAAElFTkSuQmCC)}.tiles_wrapper .icon14,.tiles_wrapper .icon16,.tiles_wrapper .icon19,.tiles_wrapper.article_tile_footer_feed_title,.tiles_wrapper.span.tiles_wrapper.icon16.tiles_wrapper.popularity_icon_gray{color:#fff!important;opacity:1!important;text-shadow:-1px -1px 1px rgba(255,255,255,.1),1px 1px 1px rgba(0,0,0,.5)}.tiles_wrapper .article_content{padding-top:25px}.pin_container img{height:auto!important}.tiles_wrapper div.article_tile{border:1px solid #131313!important;box-shadow:none!important;border-radius:10px!important}div.article_footer_buttons_active>span.icon16{color:#ed374d}#sinner_container,.ad_title,.ad_title_centered,.block_article_ad{opacity:0!important}.block_article_ad{position:absolute;height:1px!important}.block_article_ad iframe{height:0!important;margin:0!important;padding:0!important}.ad_size_leaderboard,.ad_title,.ad_title_centered,.block_article_ad,.leaderboard_ad{height:0!important;margin:0!important;padding:0!important}#sinner_container{width:0!important;margin:0!important;padding:0!important}
