style.css:
@-moz-document domain("community.librenms.org") {
body {
    background-color: #21252D;
    color: #fff;
    font-family: Arial,sans-serif;
  }

h1, h2, h3 {
    background-color: #21252D;
    color: white; 
}

.docked .d-header {
    background-color: #2a2f3a;
    color: #ffffff;
}

#topic-title h1 a {
    color: #ffffff;
}

.topic-category .badge-wrapper {
    color: #ffffff;
}

.badge-wrapper .badge-category .category-name {
    color: #ffffff;
}

#suggested-topics .topics a {
    color: #ffffff
}

.latest-topic-list-item .main-link .top-row a {
    color: #ffffff;
}

.category-list .category-text-title {
    color: #ffffff;
}

.nav-pills>li>a {
    color: #ffffff;
}
}
