style.css:
@-moz-document domain("twitchls.com") {
body, .highlight a, .highlight { background-color: #141D26!important; color: #ccc; text-decoration: none; }
.c-input { color: #ccc; }
.navbar-default, footer, .dropdown-menu, .card { background-color: #11131a; }
.navbar { border-bottom: #1b1b1b 1px solid; }
footer { border-top: #1b1b1b 1px solid; }
.logo, .selected-tag, .dropdown-menu, .dropdown-menu > li > a { color: #8798A5!important; }
.dropdown-toggle { background: #11131a!important; border: 1px solid rgb(17, 19, 26); }
.btn-primary { color: #8798A5; background-color: #11131a; border-color: #17181b; }
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover, .btn-primary.active, .btn-primary:active, .open > .btn-primary.dropdown-toggle { color: #8798A5; background-color: #17181b; border-color: #17181b; }
.card-text, .card-title {overflow: hidden;}
.card-inverse .card-blockquote, .card-inverse .card-footer, .card-inverse .card-header, .card-inverse .card-title { color: #8798A5; }
.form-control { color: #8798A5; background-color: #11131a; border: 1px solid #141d26; }
.form-control:focus { border-color: #141d26; }
.tw-root--theme-light .embed-chat-page { background: #141D26!important; color: #8798A5!important; }
.tw-root--theme-light .tw-c-background-alt { background-color: #141D26 !important; }
.tw-root--theme-light .tw-border-b { border-bottom: 1px solid #141D26 !important; }
.tw-root--theme-light .tw-c-background-base { background-color: #141D26 !important; }
.text-fragment { color: #8798A5!important; }
.tw-root--theme-light .tw-textarea { background: #1B2836!important; border: 1px solid #11131a!important; color: #8798A5!important; }
.tw-root--theme-light .tw-c-text-base, .tw-root--theme-light .tw-c-text-alt { color: #8798A5 !important; }
.card { margin-bottom: 20%; }
.card-footer { position: absolute; bottom: -30%; }
.bubirurlha { background: #0a0e13; margin: 0 3px 0 3px; color: #8798A5; padding: 1px 8px; position: relative; top: -8px; }
.bubirurlha:hover, .bubirurlha:active, .bubirurlha:focus { background: #010202; text-decoration: none; color: #8798A5; }
}

@-moz-document url-prefix("https://www.twitch.tv/embed") {
.tw-root--theme-light .chat-room, .tw-c-background-base { background: #141D26!important; }
.tw-c-text-alt, .tw-c-text-base, .cMQeyU, .tw-textarea::placeholder, .tw-input::placeholder, .tw-input, .tw-textarea, .tw-link--inherit, .tw-link--inherit:visited, .tw-link--inherit[data-focus-visible-added] { color: #8798A5 !important; }
.tw-input, .tw-input:hover, .tw-select, .tw-select:hover, .tw-textarea, .tw-textarea:hover { background-color: #11131a!important; }
.tw-c-background-alt { background-color: #11131a!important; }
.tw-core-button--secondary { background-color: #11131a!important; color: #8798A5 !important; }
.tw-core-button--primary.tw-core-button--disabled { background-color: #11131a!important; color: #8798A5 !important; }
}
