style.css:
@-moz-document domain("duelingnexus.com") {
/*change this link to change the background*/

html ,#game-rps-container{
	background: url(https://imgur.com/OMmsFjQ.png )!important;
}


/*this changes the image that indicates that you can special summon from Extra Deck 

 or Graveyard*/

.game-field-zone-activate {
	content:url("https://i.imgur.com/rCcLieN.png");
}


/* here you can change card sleeves (only you can see them)*/

.game-field-card[src="assets/images/cover.png"] {
	 content: url('https://imgur.com/X4i2M6i.png');
	height:93px;
	width:64.8px;
}

/*change your avatar (only you can see it)*/

#game-avatar-player-image{
	content: url('https://imgur.com/tmyXiAB.png');
	height:76.39px;
	width:76.39px;
	
}















* {
	border-radius:0px 10px 10px 10px;
}

.game-field-card, .editor-card-small{
	border-radius:0;
}


.game-field-zone {
	border-radius:0;
	
}

#editor-main-deck, #editor-extra-deck, #editor-side-deck {
	border:1px solid #ffffff47;	
}

#navbar-left , #navbar-right {
	position:fixed;
	z-index:5;
}

#navbar-middle {
	position:fixed;
	padding-left:35%;
	top:0%;
}

.game-selection-card {
	margin: 1vh 1vw 1vh 1vw;
	z-index: 7;
}

.game-field-card {
	z-index: 10;
}

.game-field-stats-text {
	color: #fff !important;
	z-index: 3;
}

.game-field-zone-content  {
	color: #fff;
	z-index: 5;
}

.game-card-negated {
	z-index: 5;
}

.game-field-zone-activate {
	z-index: 8;
	position: absolute;
}


#game-option-list {
	padding: 0.5vw
}

#game-option-list .engine-button {
	margin: 0.25vw;
	padding: 0;
	position: relative;
	bottom: 0;
}

#game-tooltip    {
	color: #fff ;
	font-size: 1.05vw;
	background: radial-gradient(#00000070, #0a0202);
	border: 1px dashed #000;
	padding: 1vh;
	z-index: 7;
	position: fixed;
}

#downloadDeck {
	background-color: #19660c
}

#card-description-box {
	background: rgba(0,0,0,0.55);
}

#game-alert-window,
#options-window,
#game-results-container,
#game-message-window,
#game-waiting-menu,
#game-selection-window,
#game-yesno-window,
#game-position-window,
#game-option-window,
#game-announce-window,
#game-announce-card-window
{
	background-color: #00000080;
	border-color: #fff;
	border-width: 15px 1px 1px 3px;
	border-radius: 15px;
	left:-15vw;
	top:40vh;
	
}

#game-end-window{
	background-color: #00000080;
	border-color: #fff;
	border-width: 15px 1px 1px 3px;
	border-radius: 15px;
}

#game-room-container {
	width: 60vw;
	min-width: 10px;
	padding-bottom: 0px;
	padding-top: 0px;
	padding-left: 0px;
	padding-right: 0px;
}

#game-room-players {
	width: 40vw;
	min-width: 28px;
	padding: 2vh 2vw 2vh 1vw;
	margin-top: 20px;
}

#game-room-information {
	width: 16vw;
	min-width: 260px;
	padding: 2vh 2vw 2vh 1vw;
}

#game-selection-window {
	overflow-y: scroll;
	height: 25vw;
	width: 40vw;
	z-index: 6;
	left:15vw;
	top:30vh;
}

#game-deck-selection {
	border: solid #fff;
	border-width: 12px 1px 1px 3px;
	border-radius: 11px;
	overflow-y: scroll;
	height: 300px;
	width: 450px;
	min-width: 120px;
	margin-top: 7px;
	padding: 5px;
	z-index: 2;
	position: absolute;
	}

#options-area {
	position: fixed;
	right: 0.8vw;
	bottom: 25px;
}

#options-window {
	height: 23vh;
	width: auto;
	overflow-y: visible;
	z-index: 10;
	left:65vw;
	top:60vh;
	
}

#game-siding-column{
	height: 37.67vw;
	width: 33.33vw;
}

#game-siding-column-2{
	border: 1px;
	height: 37.67vw;
	width: 37.67vw;
}

#game-siding-done{
	height: 4vw;
	width: 100%;
	position: relative;
	top: 5vw;
}

 .game-field-zone
  {
    background: repeating-radial-gradient( #0000007d , #000000a6 );
    border: 3px solid #fff0;
    border-style: outset;
  }


	
#my-profile , #home-area ,
#duel-area , #decks-area
, #gamelist-area {
	background-color:#010101b5;
}

#news-area {
	background:#000000b5;
}



body{
font-size: 14px;
}

#game-field-extra-monster1:hover, #game-field-extra-monster2:hover, #game-field-

player-monster1:hover, #game-field-player-monster2:hover, #game-field-player-

monster3:hover, #game-field-player-monster4:hover, #game-field-player-

monster5:hover, #game-field-extra-monster1:hover, #game-field-opponent-

monster1:hover, #game-field-opponent-monster2:hover, #game-field-opponent-

monster3:hover, #game-field-opponent-monster4:hover, #game-field-opponent-

monster5:hover {
    
    background-color: #fff9;
}

#game-field-player-spell1:hover, #game-field-player-spell2:hover, #game-field-

player-spell3:hover, #game-field-player-spell4:hover, #game-field-player-

spell5:hover, #game-field-opponent-spell1:hover, #game-field-opponent-spell2:hover, 

#game-field-opponent-spell3:hover, #game-field-opponent-spell4:hover, #game-field-

opponent-spell5:hover{
    background-color: #fff9;
}

#game-field-opponent-graveyard:hover,#game-field-player-graveyard:hover,#game-

field-opponent-spell6:hover,#game-field-player-spell6:hover, #game-field-player-

banished:hover,#game-field-opponent-banished:hover,#game-field-player-deck:hover, 

#game-field-opponent-deck:hover, #game-field-player-extra:hover, #game-field-

opponent-extra:hover{
    background-color: #fff9;
}


#rs-ext-editor-export-button{
	position:relative;
	left:7vw;
}
}

@-moz-document domain("duelingnexus.com") {

/*delete this code section if you are having problems with the 3D game field */

#game-selection-window {
	transform:translate(-19vw , -11vh)
}

#game-yesno-window{
	transform:translate(-4vw)
}

#game-container {
	perspective:2100px;
}

#game-column{
	transform:rotatex(45deg) translatex(-20px) scale(0.9,1.02);
		
}

#game-action-menu{
	transform:translate(-27.5vw, -3vh)
}

#game-tooltip {
	transform:translatex(-27vw);
		background:rgba(0, 0, 0, .6);
}

.game-avatar-area {
	transform: scale(1.15) 
}

#game-rps-container , #game-rps-result{
	perspective:1700px;
}

#game-rps-scissors {
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}

#game-rps-paper {
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}

#game-rps-rock{
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}
}
