style.css:
@-moz-document domain("whistlinginthewind.org") {
body,
#page {
    background-color: #222;
    color: azure;
}

.entry-title a,
.entry-footer {
    color: azure;
}

.entry-content {
    font-size: 1.05rem;
}

.site-branding .site-title a,
.site-description,
.main-navigation a,
.fn,
time,
.post-title,
.post-navigation .meta-nav {
    color: azure;
}

.post-navigation,
.post-navigation div + div {
    border-top: 2px solid azure;
    border-bottom: 2px solid azure;
}

.widget {
    border-top: 4px solid azure;
    color: azure;
}

.widget-title a,
h2,
#twitter_3 {
    color: azure !important;
}

.wp-caption-text {
    color: azure !important;
}

.widget li {
    font-size: .9rem;
}
}
