style.css:
@-moz-document domain("rubyonrails.org") {
body {
    background: #333;
}
p {
    color: #E2E2E2;
}
#container {
    color: white;
    background-color: #333;
}
#subCol {
    background: #F92672;
    color: black;
}
#subCol .chapters {
    color: inherit;
}
#mainCol a,
#subCol a,
#feature a {
    color: inherit;
}
#mainCol a code,
#subCol a code,
#feature a code {
    color: lightgray;
}
.note {
    background: #222222 url(http://guides.rubyonrails.org//images/tab_note.gif) no-repeat left top;
}
.info {
    background: #718896 url(http://guides.rubyonrails.org//images/tab_info.gif) no-repeat left top;
}
div.code_container {
    background: #6D6666 url(http://guides.rubyonrails.org//images/tab_grey.gif) no-repeat left top;
}
pre,
code {
    color: #ddd;
}
table {
    background: transparent;
}
table th {
    background: darkgreen;
}
.description pre {
    background: #222222;
}
.sectiontitle, .title, .methodtitle, li, .aka, .description h3, .attr-name {
    color: #dddddd;
}
p code { color: #333333; }
pre .keyword { color: #66D9EF; }
pre .number, pre .subst { color: #A6E22E; }
pre .identifier { color: #F92672; }
pre .string { color: #FD971F; }
}
