.data a:hover,
.def,
.h1 a:hover,
.h2 a:hover,
.h3 a:hover,
.h4 a:hover,
.h5 a:hover,
.main_nav a:hover,
.menu a:hover,
.secondary_nav,
.toc-sidebar,
.ui-tabs .ui-tabs-nav li,
.w3c_toc a:hover,
:focus,
:hover,
