#side-cart-cell,
.account-widget,
.bdytext,
.bodycontainer,
.listings-container,
.ramobile .tree-bottom-wrapper,
.whitebubble {
	margin: 12px!important;
}

table.tblz {
	margin: 1rem auto!important;
}

