.altrow-a-1,
.bdytext,
.content,
.listings-container,
.moreinfotable td,
.ra-dialog,
.ra-widget-panel,
.ramobile .mtf-outer-list,
.whitebubble,
table.tblz {
	padding: 12px!important;
}

table.overlaypartcomparison td {
	padding: 4px;
}

.ramobile input,
.ramobile select,
.ramobile textarea {
	padding: 6px!important;
}

.ranavouter {
	padding: 5px;
}

.top-tab-cart-bw-b {
	padding: 27px!important;
}

.ramobile table {
	border-collapse: separate;
	border-spacing: 1px;
}

/*___ Heights ______________________________________________________________________*/

ppp {
	max-height: none!important;
}

.ra-dialog {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.bodycontainer,
[style="padding-top:5px; max-width:1350px"] {
	max-width: none!important;
}

.bodycontainer {
	width: 98%!important;
}

[style="padding-top:5px;padding-left:5px;"][width="233"] {
	width: 10%!important;
}
}

