style.css:
@-moz-document domain("rubyrealms.com")
{

.label.alert {
    background: /*[[color]]*/;
    color: #fefefe;
}

.badge.alert {
    background: /*[[color]]*/;
    color: #fefefe;
}

:root {
    --color-background-1: #363636;
    --color-background-1-rgba: rgba(54, 54, 54, 0.7);
    --color-background-2: #272727;
    --color-background-2-rgba: rgba(39, 39, 39, 0.7);
    --color-background-xp: #c91022;
    --color-background-green: #14a76c;
    --font-color-1: white;
    --font-color-2: #747474;
    --font-color-3: /*[[color]]*/;
}

.wall-comment-title div i {
    color: /*[[color]]*/;
    cursor: pointer;
}

.forum-topic-threads span {
    text-align: center;
    padding: 5px 0px;
    border-radius: 5px;
    background: #272727;
    color: /*[[color]]*/;
    padding: 5px;
}

.head {
    border-bottom: 2px solid /*[[color]]*/;
}

.background-container {
    border-bottom: 2px solid /*[[color]]*/;
}

#category-tabs a[aria-selected='true'] {
    background-color: /*[[color]]*/;
}

.tabs-content {
    border-bottom: 2px solid /*[[color]]*/ !important;
}

.color-background-5 {
    border-bottom: 2px solid /*[[color]]*/;
}

}
