style.css:
@-moz-document domain("lovehug.net"), domain("weloma.net")
{
	body>br,
	body>iframe,
	#myCarousel,
	#report_error,
	#sbottom,
	#fb-root,
	#modal-1,
	#content>center,
	#contentstory>br,
	#contentstory>center,
	#content>br,
	#rd-side_icon,
	.col-lg-4> :not(div),
	.chapter-content br,
	.chapter-content center,
	.clear-fix,
	.like-box,
	.float-ck,
	.seoonpage,
	.row-list hr,
	.ads,
	.adtrue-div,
	.quang-cao,
	.end,
	.modal-backdrop,
	.mrb10,
	.sticky_bottom,
	.doreamon>center,
	.panel>center,
	center>br,
	a.chapter[href="#"],
	div[id^="rn_ad_"] {
		display: none !important;
	}
	
	::-webkit-scrollbar {
		width: 2pt;
		height: 2pt;
		background: #000
	}
	
	::-webkit-scrollbar-thumb {
		background-color: #c48;
	}
	
	body {
		height: auto;
		min-height: 100vh;
		font: 400 12pt sans-serif;
		padding-top: 4mm;
		background:
			linear-gradient(87deg,
				rgba(20, 20, 30, .8) 0,
				rgba(20, 20, 30, .5) 35%,
				rgba(20, 20, 30, .4) 55%,
				rgba(20, 20, 30, .8) 100%),
			linear-gradient(165deg,
				rgba(30, 30, 30, .7) 0,
				rgba(30, 30, 30, .4) 20%,
				rgba(30, 30, 30, .6) 70%,
				rgba(30, 30, 30, .8) 100%);
		overflow-x: hidden;
	
	}
	
	h1,
	.h1 {
		font-size: 14pt;
		margin: .3em 0
	}
	
	h2,
	h3 {
		margin: .5em 0 .3em
	}
	
	img {
		object-fit: contain;
	}
	
	time {
		display: inline-block;
		min-width: 7em;
	}
	
	#header .navbar-collapse:not(:last-of-type) {
		float: right;
		width: auto;
	}
	
	.search {
		max-height: 36px;
		margin: .4em 2em 1em 0;
		display: block;
		overflow: hidden;
		z-index: 99;
		opacity: .7;
		transition: max-height .6s, opacity .5s .6s;
		box-shadow: 0 1mm 3mm #333;
	}
	
	.search:hover {
		max-height: 50vh;
		overflow-y: scroll;
		transition: max-height .3s, opacity .3s;
	}
	
	ul.ss-box li.ss-result a {
		padding: 0;
	}
	
	.ss-result table {
		min-height: 70px;
	}
	
	#pop_manga {
		display: none !important;
		border-radius: 2mm;
		overflow: hidden;
		box-shadow: 0 0 3mm #333;
	}
	
	.navbar,
	.headroom:not(.chapter-content) {
		display: block !important;
		top: -40px !important;
		height: auto !important;
		opacity: .5;
		box-shadow: 0 0 3mm #555;
		transform: none !important;
		transition: all .8s 0.6s;
		backdrop-filter: blur(5mm);
		-webkit-backdrop-filter: blur(5mm);
	}
	
	.navbar:hover,
	.headroom:hover {
		top: 0 !important;
		z-index: 100 !important;
		opacity: .95;
		transition: all .1s;
	}
	
	.navbar {
		margin: 0;
	}
	
	.chapter-before .chapter-select {
		position: fixed;
		left: 0;
		bottom: -45px;
		width: 100vw;
		padding: 5px 0;
		background-color: rgba(60, 60, 60, .8);
		opacity: .6;
		z-index: 9999;
		box-shadow: 0 0 2mm rgba(0, 0, 0, .7);
		transition: .8s .4s;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm);
	}
	
	.chapter-before .chapter-select:hover {
		bottom: 0;
		background-color: rgba(135, 140, 140, .6);
		opacity: 1;
		transition: .2s;
	}
	
	body>.container,
	.mCustomScrollbar>.container {
		position: relative;
		min-width: 670px;
		padding: 0 3mm;
		margin: 0;
	}
	
	.owl-carousel {
		max-width: 1200px;
		margin: 0 auto;
	}
	
	#contentstory,
	.backgroundhide {
		margin: 0 0 3mm;
	}
	
	.doreamon {
		font-size: 11pt;
		padding: 0 .5em;
	}
	
	.doreamon:after {
		display: block;
		content: "";
		height: 1.5mm;
		clear: both;
	}
	
	.chapter-content-top {
		margin: 0;
	}
	
	.carousel-inner>img {
		height: 100px;
		overflow: hidden
	}
	
	.media-object {
		object-fit: cover;
	}
	
	#content,
	.chapter-content {
		font-size: 0;
		width: auto !important;
		max-width: calc(96vh / 1.4);
		text-align: center;
		direction: rtl;
		margin: 0 auto;
	}
	
	.chapter-select {
		direction: ltr;
	}
	
	.swiper-container {
		height: 94vh;
		padding-top: 0;
	}
	
	.swiper-container,
	.swiper-wrapper {
		min-height: calc(100vh - 52px) !important;
	}
	
	.swiper-wrapper,
	.swiper-container .swiper-slide,
	.swiper-container .chapter-img {
		max-height: 100% !important;
	}
	
	.list-chapters .list-wrap p {
		margin: 0;
	}
	
	.list-chapters .title {
		display: inline-block;
		line-height: 2;
		width: calc(100% - 260px);
	}
	
	.swiper-wrapper .white-slide {
		background-color: rgba(255, 255, 255, .4);
	}
	
	.chapter-pagination {
		top: 0;
	}
	
	#content>img,
	.chapter-content .chapter-img {
		min-width: 60vh;
		max-width: 100%;
		height: calc(100vh - 19mm);
		background: none;
		border: none;
		border-radius: 3mm;
		margin: 0 auto 14px;
	}
	
	.chapter-content .well {
		width: 80vw;
		min-width: 500px !important;
		max-width: 900px;
		margin: 0 auto;
		padding: 0 1em;
		border-radius: 3mm;
	}
	
	.page-header {
		margin: 0;
		padding: 0;
	}
	
	.container.list,
	.mCustomScrollbar {
		padding-top: 0;
		max-width: 100vw;
		height: auto !important;
		overflow-x: hidden;
	}
	
	.mCustomScrollbar {
		overflow: hidden;
	}
	
	.btn {
		font-size: 10pt;
		margin: 1px 2px 1px 0;
	}
	
	.btn-xs {
		color: #ddd;
		line-height: 1.6;
		padding: .2em;
		border: none;
		padding: 0 .4em;
		border-radius: 4px;
	}
	
	.btn-xs:hover {
		color: #fff;
	}
	
	.hinhnen1 {
		padding: 3mm;
	}
	
	.col-lg-8,
	.info-manga {
		min-width: 600px;
		margin: 0 !important;
	}
	
	.col-lg-4>div {
		width: auto;
		display: block;
	}
	
	.col-lg-4 .panel-body {
		column-count: 2;
	}
	
	.col-lg-4 .panel-body .pull-left,
	.col-lg-4 .panel-body .pull-right {
		float: none !important;
		display: inline;
	}
	
	.breadcrumb {
		text-align: left;
		padding: 1mm 2mm;
		margin: 0 0 1mm;
		border-radius: 1em;
		background-color: rgba(0, 0, 0, .5);
		box-shadow: 0 0 1mm #888;
		white-space: nowrap;
		overflow: hidden;
	}
	
	.breadcrumb>li+li:before {
		color: #c61;
		content: ">"
	}
	
	.breadcrumb a,
	.itemupdate a.chapter {
		display: inline-block;
		padding: 0 1.5mm;
		border-radius: 1.5em;
		text-decoration: none;
	}
	
	.col-lg-4 .panel-body a {
		display: inline-block;
		padding: 2px 2mm;
		margin: 2px 0;
		line-height: 1;
		border-radius: 1em;
		text-decoration: none;
	}
	
	.breadcrumb a:hover,
	.itemupdate a.chapter:hover,
	.col-lg-4 .panel-body a:hover {
		color: #fff;
		background-color: #825;
	}
	
	.xt {
		clear: both;
	}
	
	.btn-info {
		margin: 0 0 1.5mm;
	}
	
	.select-chapter {
		width: auto;
		min-width: 50mm;
		padding: 1mm 5mm 0;
	}
	
	.pagination {
		margin: 3mm 0 0;
	}
	
	.title_doctruyen .centertext a.link_right {
		font-size: 18pt;
		padding: 0 25%;
		margin: 0 0;
		display: block;
	}
	
	.container.manga {
		padding-top: 0;
	}
	
	.cmt>.col-md-8 {
		max-width: 1280px;
		float: none;
		margin: 0 auto;
	}
	
	.fb_iframe_widget {
		white-space: nowrap;
		width: auto !important;
		min-width: 0 !important;
	}
	
	#myTabContent {
		padding: 0 5mm;
	}
	
	.tieude {
		margin: 0 auto !important;
	}
	
	img[src="/images/download1.gif"] {
		display: inline-block;
		width: auto;
		height: 7mm;
	}
	
	.end2 {
		margin-top: 2mm;
	}
	
	.chapter-content .chapter-img {
		display: inline-block;
	}
	
	#content>img:first-of-type,
	.chapter-img:first-of-type {
		display: block;
	}
	
	
	@media screen {
		select {
			font: 12pt sans-serif;
			line-height: 1.2;
		}
	
		.thumb-item-flow {
			flex: none;
			width: calc(100% / 4);
			padding: 3mm !important;
			margin: 0 0 1mm;
			border-radius: 2mm;
			opacity: .85;
			transition: .2s;
		}
	
		.thumb-item-flow:hover {
			background: rgba(0, 0, 0, .6);
			box-shadow: 0 0 2mm #f0a;
			opacity: 1;
		}
	
		.itemupdate {
			padding: 0;
		}
	
		.itemupdate ul.group {
			padding: 0 2mm;
			margin: 0;
		}
	
		.search-box .input-group {
			width: 300px;
		}
	
		.suggestsearch {
			border: none;
		}
	
		.media:first-child {
			padding: 0;
		}
	
		.sidebar-content {
			clear: both;
			height: 120px;
			padding: 0 0 3mm;
		}
	
		.sidebar-content>a {
			width: 100px;
			height: 100%;
			display: inline-block;
			padding: 0;
		}
	
		.sidebar-content>a img {
			width: 100%;
			height: 100%;
			margin: 0 auto;
			object-fit: cover;
		}
	
		.modal-open {
			overflow: auto;
		}
	
		.container {
			max-width: 100vw;
		}
	
		.suggestsearch {
			width: 370px;
		}
	
		.itemupdate,
		.row-list {
			height: calc(13.2vh + 2.5em);
			min-height: 200px;
		}
	
		img.myimg,
		.doreamon .cover img,
		img.media-object.img-thumb {
			height: 13.2vh;
			min-height: 160px;
		}
	
		.info-manga .card-body {
			max-height: 20em;
			transition: .3s;
			overflow: hidden;
		}
	
		.btn-success {
			background: #188;
		}
	
		.btn-warning {
			background: #972;
		}
	
		ul.list-chapters {
			max-height: none;
		}
	
		.info-manga .card-body::after {
			position: absolute;
			bottom: 0;
			left: 0;
			content: "";
			width: 100%;
			height: 50%;
			background: linear-gradient(0deg,
					rgba(30, 30, 40, .6) 0,
					rgba(30, 30, 40, 0) 100%);
			border-radius: 0 0 2mm 2mm;
			z-index: 3;
			transition: .3s .3s;
		}
	
		.info-manga .card-body:hover {
			max-height: 300em;
			overflow: auto;
			transition: .5s;
		}
	
		.info-manga .card-body:hover:after {
			height: 0;
			transition: .3s;
		}
	
		ul.list-chapters a li .chapter-name,
		ul.list-chapters a li .chapter-time {
			font-size: 13pt;
		}
	
	}
	
	@media screen and (max-width: 992px) {
	
		.row>.col-md-8:first-child,
		.col-lg-8,
		.info-manga {
			width: 100%;
		}
	
		.row>.col-md-4:last-child,
		.col-md-4,
		.col-lg-4 {
			position: static;
			width: 100%;
			padding: 6px 6px 0;
			margin: 2mm 0 0;
			overflow: hidden;
			border-radius: 2mm 0 0 2mm;
			box-shadow: 0 0 1mm #666;
		}
	
		.col-lg-4 .panel-body {
			column-count: 4;
		}
	}
	
	@media screen and (min-width: 992px) {
		.container.manga .col-md-8.info-manga {
			width: 66.66%;
		}
	
		.row>.col-md-8:first-child,
		.col-lg-8,
		.info-manga {
			width: calc(100vw - 100px);
		}
	
		.row>.col-md-4:last-child,
		.col-lg-4 {
			position: absolute;
			width: 350px;
			max-width: 350px;
			right: -260px;
			padding: 1em;
			margin: 0;
			background-color: rgba(200, 200, 200, .8);
			overflow: hidden;
			border-radius: 2mm 0 0 2mm;
			box-shadow: 0 0 1mm #666;
			opacity: .5;
			z-index: 3;
			transition: all .8s;
		}
	
		.row>.col-md-4:last-child:hover,
		.col-lg-4:hover {
			right: 0;
			opacity: 1;
			box-shadow: 0 0 2mm #444;
			transition: all .3s .2s;
			backdrop-filter: blur(5px);
			-webkit-backdrop-filter: blur(5px);
		}
	
		.row>.col-md-4:last-child .tab-pane,
		.col-lg-4 .panel-body {
			column-count: 2;
		}
	}
	
	@media screen and (max-width: 650pt) {
	
		.thumb-item-flow {
			width: calc(100% / 2);
		}
	
		.itemupdate,
		.row-list {
			width: calc(100% / 2 - 7px);
		}
	}
	
	@media screen and (min-width: 650pt) {
	
		.thumb-item-flow {
			width: calc(100% / 4);
		}
	
		.itemupdate,
		.row-list {
			width: calc(100% / 3 - 7px);
		}
	}
	
	@media screen and (min-width: 900pt) {
		.thumb-item-flow {
			width: calc(100% / 6);
		}
	
		.itemupdate,
		.row-list {
			width: calc(100% / 4 - 7px);
		}
	}
	
	@media screen and (min-width: 1200pt) {
		.thumb-item-flow {
			width: calc(100% / 8);
		}
	
		.itemupdate,
		.row-list {
			width: calc(100% / 5 - 7px);
		}
	
		.itemupdate,
		.row-list {
			height: calc(17.5vh + 2.5em);
		}
	
		.col-md-8 .a6-ratio {
			height: calc(33vh - 44mm);
			min-height: 250px;
		}
	
		.img-in-ratio {
			background-size: cover
		}
	
	}
	
	@media screen and (min-width: 1700pt) {
		.thumb-item-flow {
			width: calc(100% / 10);
		}
	
		.itemupdate,
		.row-list {
			width: calc(100% / 7 - 7px);
		}
	
		.itemupdate,
		.row-list {
			height: calc(22vh + 2.5em);
		}
	
		.media-body {
			margin-left: 55%;
		}
	
		.media-body .media-heading {
			padding-left: 55%;
			text-indent: -122%;
		}
	
		.row-list .pull-left,
		.itemupdate>a.cover {
			width: 55%;
		}
	
		.itemupdate .group .title-h3 {
			padding-left: 57%;
			text-indent: -132%;
		}
	
		.itemupdate .group>li:not(:first-child) {
			padding-left: 57%;
		}
	
		.col-md-8 .a6-ratio {
			height: calc(50vh - 60mm);
			min-height: 310px;
		}
	
		.img-in-ratio {
			background-size: cover
		}
	}
	
	@media screen and (min-width: 133vh) {
	
		#content,
		.chapter-content {
			max-width: calc(96vh * 1.4);
		}
	
		.chapter-content .chapter-img {
			max-width: 49.9%;
		}
	
		.chapter-content .chapter-img:nth-of-type(even):not(:last-of-type) {
			border-radius: 0 3mm 3mm 0;
		}
	
		.chapter-content .chapter-img:nth-of-type(odd):not(:first-of-type) {
			border-radius: 3mm 0 0 3mm;
		}
	}
}
