style.css:
@-moz-document domain("vimcasts.org") {
body {
    background-color: #2f2f2f;
    color: #ffffff;
}

h1, h2, h3, h4, h5, h6 {
    color: #dddddd;
}

code {
    color: #999999;
}

.episode_number, time.pubdate{
    color: #f5f5f5;
}

a {
    color: #e65940;
}

a:hover, a:focus {
    color: #ff2f0b;
}

.panel {
    background-color: #3f3f3f;
}

.panel p {
    color: #f1f1f1;
}

.testimonial p, blockquote p {
    color: #d1d1d1;
}


}
