style.css:
@-moz-document domain(makegamessa.com) {
    body {
        color:#fff;
    }
    .logo {
        background:url('https://i.imgur.com/7J240hs.png') no-repeat center;
        width:305px;
        height:62px;
    }
    .logo img {
        display:none;
    }
    .navbar-main {
        background:#333;
    }
    .navbar-main .nav>li>a {
        color:#fff;
    }
    .content-background {
        background:#444;
    }
    .Item {
        background-color:#666;
    }
    .Item .Title a {
        color:#fff;
    }
    .Item .Title a:hover {
        color:#f00;
    }
    .ItemDiscussion .DiscussionCommentCount a {
        color:#fff;
    }
    .ItemDiscussion .DiscussionCommentCount a:hover {
        color:#f00;
    }
    .Item .Meta a {
        color:#e44824;
    }
    .Item .Meta a:hover {
        color:#fff;
    }
    .Item .Username {
        color:#fff;
    }
    .PanelCategories {
        background-color: #666;
    }
    .PanelCategories li .ItemLink {
        color:#fff;
    }
    .PanelCategories li .ItemLink:hover {
        color:#f00;
    }
   .Message blockquote .QuoteText {
      background-color:#555; 
   }
  .Message blockquote .QuoteText .QuoteText {
     background-color:#444; 
  }
  .MessageForm .FormWrapper {
   	 background-color:#666;
  }
  .MessageForm .Buttons {
   background-color:#666;
    border-top:1px solid #888;
  }
   .MeBox .FlyoutButton {
     background-color:#eee;
  }
  .MeBox .FlyoutButton:hover {
    background-color:#ec3d20;
  }
    .page-footer {
        background: #444;
        color: #fff;
    }
  .Popup .Content {
    background-color:#555;
  }
}
