/*Прокрутка*/
::-webkit-scrollbar {
    width: auto; }

::-webkit-scrollbar-track {
    background: var(--kr1) !important; }

::-webkit-scrollbar-thumb {
    background: var(--kr2) !important; }

::-webkit-scrollbar-corner {
    background: transparent !important; }

/*Загрузка*/
[src*="/img/loading.gif"] {
    content: url(http://d.zaix.ru/hgx3.gif); }

/*Ссылки, линии*/
a {
    color: var(--cl1); }

hr {
    border: 0.5px solid var(--hr1) !important; }

a:hover {
    color: var(--cl2); }

/*Ползунки*/
.ui-slider {
    background: var(--ui1) !important;
    border: 0.5px solid var(--ui1) !important;
    margin-right: 50px; }

.ui-slider .ui-slider-handle {
    background: var(--ui2) !important;
    border: 0.5px solid var(--ui2) !important; }

/*Переходы*/
.move_name {
    color: var(--pr1) !important; }

/*Убирает мусор*/
.title {
    display: none; }

#main_table {
    border: 0px;
    margin-top: 10px; }

table#info_main {
    border-radius: 5px;
    border: 2px solid var(--fm1); }

/*ТБ*/
#app > p:not([id]) {
    position: relative;
    text-align: center;
    font-weight: bold;
    font-size: 14px !important;
    border-radius: 10px;
    padding: 10px;
    background-color: var(--tb1);
    color: var(--tbTX);
    size: 18px; }

/*ТОС*/
#tr_tos {
    background: var(--ts1); }

#tos {
    margin-top: 5px;
    height: 15px;
    border-radius: 10px; }

/*Ошибка-объявление*/
p#error {
    top: 355px;
    left: 65px;
    background-color: var(--er1);
    color: var(--erTX);
    border: 2px solid var(--ui2);}

/*Тело*/
body {
    background-color: var(--bd1) !important;
    color: var(--bdTX); }

/*Параметры, навыки, локация, рот*/
#parameter {
    position: fixed;
    width: 183px;
    top: 20px;
    left: 65px;
    border-radius: 10px;
    text-align: left;
    padding: 6px;
    color: var(--prTX);
    background-color: var(--lc1);
    border: 2px solid var(--ui2); }

a#parameters.toggle {
    display: none; }

#location {
    color: var(--lcTX) !important; 
    position: fixed;
    width: 183px;
    top: 285px;
    left: 65px;
    background-color: var(--lc1);
    border: 2px solid var(--ui2);
    border-radius: 10px;
    text-align: center;
    padding: 5px;
    font-weight: bold;
    font-size: 14px !important; }

#tr_mouth {
    position: fixed;
    width: 330px;
    top: 240px;
    right: 25px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
    background-color: var(--mt1);
    color: var(--mtTX);
    border: 2px solid var(--ui2);}

#thdey {
    margin-left: 10px; }

div#tir {
    margin-left: 10px;
    margin-top: 10px; }

.active_thing {
    border: 1px solid var(--atTX); }

#items {
    padding-left: 17px;
    padding-top: 5px;
    padding-bottom: 5px; }

/*История*/
#history_block div {
    font-size: 0;
    padding: 8px; }

#history div {
    background-color: var(--hs1) !important;
    margin-top: 10px;
    margin-left: 5px;
    color: var(--hsTX); }

span#ist {
    margin-left: 5px; }

#history {
    background-color: var(--hs1);
    width: 100%;
    margin-left: 10px;
    border: 2px solid var(--hs1); }

/*Убирает тусклость Игровой*/
div#cages_div {
    opacity: 1 !important; }

/*Мой кот, чат, ЛС*/
.small {
    display: block; text-align:center;
    color: var(--smTX); 
    font-size: 14px; }

.small + br {
    display: none; }

#newchat {
    background-color: var(--nw1) !important;
    color: var(--nwTX) !important; }

#newls {
    background-color: var(--nw1) !important;
    color: var(--nwTX) !important; }

select {
    background-color: var(--sl1);
    color: var(--chTX); }

input {
    background-color: var(--ip1) !important;
    color: var(--ipTX) !important;
    border: 0.5px solid var(--ip1) !important;
    border-radius: 5px; }

.nick {
    color: var(--niTX) !important; }

#tr_chat {
    background-color: var(--ch1);
    padding: 0px; }

.myname {
    background-color: var(--mn1) !important;
    color: var(--mnTX); }

#chat_msg {
    background-color: var(--ch2);
    color: var(--chTX);
    border: 0px;
    overflow-x: hidden; }

#chat_form {
    color: var(--cfTX); }

select#text {
   color: var(--txTX); }

/*Действия*/
#akten {
    margin-left: 5px; }

#block_mess {
    margin-left: 10px;
    margin-top: 5px;
    margin-bottom: 5px;
    color: var(--bmTX); }

#tr_actions {
    background-color: var(--ac1);
    border: 0px; }

#block_deys {
    margin-top: 5px;
    font-size: 0; }

#mit {
    background-color: var(--mi1);
    color: var(--miTX);
    border: 0.5px solid var(--mi1);
    border-radius: 5px;
    margin-top: 18px;
    font-size: 14px; }

#mitok {
    margin-top: 2px;
    border: 0.5px solid;
    border-radius: 5px;
    font-size: 14px; }

/*Панель боя*/
.hotkey {
    background-color: var(--hk1) !important;
    color: var(--hkTX) !important;
    border: 0.5px solid var(--hk1) !important;
    border-radius: 5px; }

#fightPanel {
    background-color: var(--fp1);
