style.css:
@-moz-document url-prefix("https://todoist.com/app") {

#left_menu {
    width: /*[[left_column]]*/;
}
  
  #top_bar_inner {
    width: /*[[total_width]]*/;
    min-width: 922px;
}

#content_wrapper {
    width: /*[[total_width]]*/;
      min-width: 922px;
}

#content {
 width: calc(100% - /*[[left_column]]*/);
  min-width: 656px;
  margin-left: /*[[left_column]]*/;
}
  
 #list_holder {
    width: calc(/*[[left_column]]*/ - 1px);
}
  
#quick_find {
    margin-left: calc(/*[[left_column]]*/ + 8px);
}
  
}
