style.css:
@-moz-document domain("tweetdeck.twitter.com") {
html.dark .scroll-styled-h,
html.dark .scroll-styled-v {
    scrollbar-color: #657786 #38444d;
    scrollbar-width: thin;
}

.scroll-styled-h,
.scroll-styled-v {
    scrollbar-color: #e1e8ed #fff;
    scrollbar-width: thin;
}
}
