.background-featured,
.featured-jobs,
.job-post,
.content_help p,
.page_bottom p,
.views-exposed-form,
.views-exposed-form input,
.views-exposed-form select,
.view-display-id-keyword_search,
.view-filters,
.job-listing-page,
.sidebar-block,
.content_filter,
