@-moz-document url-prefix("https://www.census.gov/") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/


#desktopHeader #mainTabs > li > a:focus,
#desktopHeader #mainTabs > li > a:hover,
#desktopHeader .kickerLink > a,
#desktopHeader .kickerLink > a:hover,
#desktopHeader .linksDropDown .nav > li > a,
#desktopHeader .linksDropDown .nav > li.active > a,
#desktopHeader .subLink a.accessibleLink,
#desktopHeader .subLink,
#desktopHeader div.subCategoriesContainer,
.uscb-featured-list-title, #desktopHeader #mainTabs > li > a:active,
.uscb-header .uscb-header-content .uscb-header-menu .uscb-header-nav-item:hover a div,
.uscb-header .uscb-header-content .uscb-header-top .uscb-header-search:hover,
.uscb-nav-item-hover .uscb-nav-item-link-hover,
.uscb-nav-item-hover,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

div.uscb-tooltip,
.uscb-color-secondary-2,
.WasThisPageHelpfulText,
.cell1,
.cell2,
.uscb-top-pagination:first-child:before,
div#us-pop-components-container table td,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

#desktopHeader #mainTabs li div.top_header,
.uscb-color-primary,
div#us-highest-density-container table thead th,
div#us-most-populous-container table thead th,
div#world-pop-historical table td:first-child,
div#world-pop-historical table td:nth-child(3),
h1,
h2
{
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.uscb-color-secondary-1,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.uscb-top-pagination .uscb-top-pagination-links,
.uscb-top-pagination .uscb-top-pagination-links + span,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.uscb-color-black,
.uscb-color-secondary-1 span:nth-child(3),
div#us-pop-on-date span.select-date,
div#world-pop-historical table td:nth-child(2),
div#world-pop-historical table td:nth-child(4),
time
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


div.change_col .infobox {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Code _________________________________________________________________________*/

.token.punctuation {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #ff6cbe!important;
}
























}

