@-moz-document url-prefix("https://www.census.gov/") {
/*__________________________________________________________________________________*/

.uscb-text-align-center,
.uscb-nav-button-container,
.uscb-list-item {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.uscb-text-align-center:hover,
.uscb-nav-button-container:hover,
.uscb-list-item:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/


img:not(.uscb-nav-image) {
	border-radius: 22px!important;
}

.uscb-nav-button-container {
	border-top-left-radius: 22px;
	border-top-right-radius: 22px;
}

/*__________________________________________________________________________________*/


img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}


li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

selector:hover {
	opacity: 1!important;
}

}

