style.css:
@-moz-document url-prefix("https://bellard.org/") {
body {
    font-family: Georgia, Cambria, "Times New Roman", Times, serif;
    color: rgba(0, 0, 0, 0.84);
    font-size: 18px;
    line-height: 1.58;
    max-width: 728px;
    margin: 0 auto;
    padding: 24px;
}
h1 {
    font-size: 40px;
    line-height: 48px;
}
h2 {
    font-size: 24px;
    margin-top: 1.23em;
}
p {
    font-size: 18px;
    line-height: 1.58;
    margin-top: 1.56em;
}
a {
    color: inherit;
}
li {
    margin-bottom: 0.78em;
}
@media(min-width:1080px) {
    body {
        font-size: 21px;
    }
    h2 {
        font-size: 26px;
        line-height: 1.18;
        margin-top: 1.72em;
    }
    p {
        font-size: 21px;
        margin-top: 2em;
    }
}
}
