style.css:
@-moz-document domain("brickplanet.com") {
* {
    background-image: url("https://cdn.discordapp.com/attachments/603721154678489119/605774698218782730/isaac.jpg") !important;
    background-size: 350% 100%;
    animation: shake 00000.1s infinite;
    -webkit-filter: saturate(3);
    filter: saturate(1.2) contrast(1.1);
    overflow: visible !important;
}

@keyframes shake {
  10%, 90% {
    transform: translate3d(-4px, -100px, 0);
  }
  
  20%, 80% {
    transform: translate3d(110px, 0, 0);
  }

  30%, 50%, 70% {
    transform: translate3d(-10px, 0, 0);
  }

  40%, 60% {
    transform: translate3d(4px, 200px, 0);
  }
}
}
