style.css:
@-moz-document domain("www.ahnegao.com.br") {
    #content {
        background-color: #222;
    }
    p {
        color: azure;
        font-family: Cambria;
        font-size: 1.8rem;
        max-width: 80%;
        padding-left: 20px;
    }
    .scroll-to-top {
        background-color: #333;
    }
    .scroll-to-top:hover {
        background-color: #555;
    }
    #narw-next, #narw-previous {
        color: #444;
    }
    #narw-next:hover, #narw-previous:hover {
        color: #666;
    }
    #barraMTV, .negaozao, .le-logo-ahnegao, .navbar, .le-navbar-2, .le-navbar, .social-top .container, #text-22, #parceiros-sidebar, #text-16, .le-social-buttons, .le-bottom-footer, .s4fans-post, .le-publicidade {
        display: none;
    }
    .entry-content, .le-title, .le-data, .post-inner-content, #masthead, .form-search, .input-group, .secondary, .form-control.search-query, #searchsubmit, .site-footer, #colophon, #le-navbar-categorias {
        background-color: #333;
    }
    .tagcloud {
        border: none;
        background-color: #333;
        margin-bottom: 30px;
    }
    .tagcloud a {
        color: #ccc;
    }
    #searchsubmit {
        border: none;
    }
    #wp_page_numbers ul li.active_page a {
        color: azure;
        background-color: #333;
    }
    #wp_page_numbers ul li a {
        color: azure;
        background-color: #222;
    }
    #wp_page_numbers li.space {
        background-color: transparent;
        color: azure;
    }
    #wp_page_numbers ul li.active_page a:hover, #wp_page_numbers ul li a:hover {
        background-color: #444;
    }
    /*
img,
iframe {
    filter: grayscale(100%);
}
*/
    #le-navbar, .social-top, #le-navbar-2, #footer-area {
        display: none !important
    }
    body, .home, .blog {
        background-color: #222;
    }
}
