/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.extended-banner__content p,
li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

.md-browse-carousel .md-browse-carousel-control {
	opacity: 1!important;
}
}

@-moz-document domain("britannica.com") {
.md-intent-accordion .accordion--question.open::after,
.ui-icon-facebook:before,
.ui-icon-twitter:before,
[data-icon]:before {
	content: ''!important;
}

/* Arrow Down _______________________________________________________________*/

.md-intent-accordion .accordion--question::after {
	background: url(https://i.ibb.co/bLRm1pJ/down.png) no-repeat!important;
	box-sizing: border-box;
}

.md-intent-accordion .accordion--question::after {
	height: 18px!important;
	background-size: 18px!important;
}

/* Arrow Up _______________________________________________________________*/

.md-intent-accordion .accordion--question.open::after {
	background: url(https://i.ibb.co/HpDt811/up.png) no-repeat!important;
	box-sizing: border-box;
}

.md-intent-accordion .accordion--question.open::after {
	height: 18px!important;
	background-size: 18px!important;
}

/* Avatar _______________________________________________________________*/

