.CodeMirror pre,
.CodeMirror-linenumber {
    color: #fff;
}

.cm-s-default .cm-bracket {
    color: #e7e7e7;
}

.cm-comment {
    color: #a4a7b2;
}

.cm-s-default .cm-keyword {
    color: #c669ff;
}

.cm-s-default .cm-meta {
    color: #99ebff;
}

.cm-s-default .cm-number {
    color: #edd65c;
}

