#keycommands .key-group,
.CodeMirror-code div[style="position: relative;"],
.HomepageCards_root-2dP-p .HomepageCards_item-1ISuJ,
.single-item
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#keycommands .key-group:hover,
.CodeMirror-code div[style="position: relative;"]:hover,
.HomepageCards_root-2dP-p .HomepageCards_item-1ISuJ:hover,
.single-item:hover
{
	filter: brightness(100%)!important;
}

