html  {
    color: #fcfcfc;
    background-color: #333333;
}
pre {
    color: black;
}

.emoji-container {
    background-color: #333333;
}

.emoji-card {
    background-color: #222222;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, .6);
}
.emoji-card:hover {
    box-shadow: 0 10px 20px 0 rgba(0, 0, 0, .6);
}

}
