#VehicleSelector,
#homepageapwtvblog>div article>div,
.SEOProductGroup,
.boxed,
.div-container1,
.selection,
.seoprodimg div.pbrand,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

#ProductListingSerp .serp__item {
    grid-gap: 0px;
    grid-template-columns: 0fr repeat(8,1fr);
}

