@-moz-document url-prefix("https://accounts.magister.net/account/login") {
body {
	background: #131719;
}
.col-sm-6 {
    width: 100%;
}
.image-carrousel div {
	display: none;
}
.input-box, .primary-button, .angucomplete-holder, .angucomplete-holder input, .angucomplete-dropdown, .angucomplete-title, .angucomplete-title:hover, .dna-input-group, .dna-text-input{
	background: #1b2022;
	color: white;
}
.primary-button, .angucomplete-holder, .angucomplete-dropdown {
	border-color: #1b2022;
}
.primary-button:hover {
	background: #323639;
	border-color: #323639;
	color: white;
}
.password__eye {
	background: none;
}
.password__eye-icon {
	fill: white;
}
.bg3_webp, .splash-container {
	display: none;
}
.bottom, .podium_container {
	width: 100%;
}
body, .podium .dna-input, .svg-inline--fa, a, h1, .completed-challenge a {
	color: white;
}
.completed-challenge, .dna-btn-primary {
	background: #1b2022;
}
.dna-input-group, .dna-text-input {
	border-color: #1b2022;
}
.top img, .bottom img {
	filter: brightness(5) grayscale(1) contrast(2);
}
.dna-btn-primary:hover {
	background: #323639;
}
}

@-moz-document url-prefix("https://aselect.entree.kennisnet.nl") {
body {
	background: #131719;
}
footer, #footer {
	color: white;
}
#contentbox {
	background: #1b2022;
}
header h1, #header h1, #contentbox {
	color: white;
}
.error-message .header {
	background: #323639;
}
.error-message {
	border-color: #323639;
}
}

@-moz-document url-prefix("https://alkwin.magister.net/error.aspx") {
body {
	background: #131719;
}
.icon-user {
	fill: #323639;
}
.error-message {
	background: #323639;
}
.error-message::after {
	border-color: transparent #323639;
}
.error-message p {
	color: white;
}
}

@-moz-document url-prefix("https://accounts.magister.net/profile") {
.icl-app-bar, .icl-btn-primary {
	background: #323639;
}
body {
	background: #131719;
}
.profile-welcome, .icl-card, .dna-card-title, .dna-description-list-grouped dd, .dna-description-list-grouped dt, h1[_ngcontent-cjx-c2] {
	color: white;
}
.icl-card, .dna-card {
	background: #1b2022;
}
.icl-description-list-grouped, .icl-description-list dd+dt, .dna-description-list-grouped, .dna-description-list dd+dt {
	border-color: #323639;
}
.icl-btn-primary:hover {
	background: grey;
}
}
