style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://friendfeed.com/), url-prefix(https://friendfeed.com/) {
body { background: #99ccff!important; }
#mainmainsizer {direction: rtl;}
.info,.likes {direction: ltr!important; text-align: right;}
textarea.title, .comment {direction: rtl;}
input, textarea, td, select, option {font-family: tahoma,sans-serif!important; font-size: 12px!important;color:black!important ; line-height: 1.5;}
.feed .cluster .icon {left: auto!important; float: right!important;}
.feed .entry .likes {background-position: right 2px!important;}
.feed .entry .likes, .feed .entry .comment, .feed .entry .commentform, .feed .entry .expandcomment {margin-left:0!important; padding-left:0!important; margin-right:12px; padding-right: 19px; position: relative; }
#sidebar {background: #99CCFF!important;}
#sidebar a:link, #sidebar a:visited { font-size: 11px;color:white }

.friend{

background-color:#e8f1fb !important; /*Highlight your friends comments*/

}

.owner{

background-color:#ffffcc !important; /*Highlight your own comments*/



}

}
