style.css:
@-moz-document domain("xantandminions.wordpress.com") {
/*
* Author: Ew0345
* Version: 1.0
* Description: A dark theme for Xantandminions Wordpress site.
*/
}

@-moz-document domain("xantandminions.wordpress.com") {
.site-content,
.content-area,
.site-main,
article,
.entry-header,
.navigation.post-navigation,
.comments-area,
.comments-wrapper,
blockquote {
    background-color: #232323 !important;
    color: #eee !important;
    font-family: 'Open Sans', sans-serif !important;
    font-size: 16px !important;
}
.entry-title,
.entry-title a {
    color: lightslategray !important;
}
.blog-info,
.blog-credits,
#actionbar {
    display: none !important;
}
::selection {
    background: lightblue !important;
}
.entry-content span[style="color:#000000;"] a {
    color: #aaa !important;
}
}

@-moz-document url("https://xantandminions.wordpress.com/kuma-kuma-kuma-bear/") {
a:link {
    color: #bbb !important;
}
a:visited {
    color: lightblue !important;
}
}
