style.css:
@-moz-document domain("twitch.tv") {
.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: #2c2a2f;
}

.tw-root--theme-dark body {
  	font-family: Helvetica Neue,Helvetica,Arial,sans-serif!important;
    background-color: #252525
}

.tw-root--theme-dark .channel-header {
    background-color: #18181b
}

.tw-root--theme-dark .tw-c-background-base {
     background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
  background-attachment: fixed;
   
}

.tw-c-background-body {
    background-color: #0f0e11!important;
}

.tw-root--theme-dark .chat-room {
    background: #2c2a2f;
}
.tw-root--theme-dark .channel-header{
   background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}
.tw-root--theme-dark .side-nav-header{
   background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}
.tw-root--theme-dark .tw-border-b {
    border-bottom: 1px solid #d9d9de!important;
    border-bottom: var(--border-width-default) solid var(--color-border-base)!important;
    background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}
}
