style.css:
@-moz-document domain(mediengestalter.info) {

body
{ margin: 0 auto !important; width: 1000px !important; background: #222222 !important; }

span
{ color: #222222 !important; }

.spacerow
{ background: #444444 !important; }

th
{ background: #444444 !important; border: solid 0px !important; }

.forumline
{ background: white !important; }

.cat
{ background: none !important; }

.catleft
{ background: #eeeeee !important; }

.catbottom
{ background: #eeeeee !important; }

.cathead
{ background: #eeeeee !important; }


.footer
{ background: #cccccc !important; }

.navigation
{ background: none !important; }

a:link, a:visited
{ text-decoration: underline !important; }

a:hover
{ text-decoration: none !important; }

a, .row1, .postbody, .postdetails, .gensmall, .genmed, .portal, .cattitle, .mainmenu
{ font: 11px, Arial, !important; line-height: 16px !important; color: #222222 !important; text-decoration: none !important; }

.button, .post, select, .mainoption
{ background: #eeeeee !important; border: none !important }

}
